function valWeddingDate(number){var n=number;var venueName=document.getElementById("venueName-"+n).value;var weddingDate=document.getElementById("weddingDate-"+n).value;if(weddingDate==""){document.getElementById("error-"+n+"-1").innerHTML='<b>Please enter a Wedding Date</b>';}else{var today=new Date();var dd=today.getDate();var mm=today.getMonth()+1;var yyyy=today.getFullYear();if(dd<10){dd='0'+dd;}
if(mm<10){mm='0'+mm;}
var currentDate=dd+'/'+mm+'/'+yyyy;var weddingDateParts=weddingDate.split("/");var weddingDateObject=new Date(+weddingDateParts[2],weddingDateParts[1]-1,+weddingDateParts[0]);var currentDateParts=currentDate.split("/");var currentDateObject=new Date(+currentDateParts[2],currentDateParts[1]-1,+currentDateParts[0]);var weddingBuffParts=currentDate.split("/");var weddingBuffObject=new Date(+weddingBuffParts[2],weddingBuffParts[1]-1,+weddingBuffParts[0]);var weddingBuffObject;weddingBuffObject.setDate(weddingBuffObject.getDate()+14);if(weddingDateObject<currentDateObject){document.getElementById("error-"+n+"-1").innerHTML='<b>Please enter a Valid Date</b>';}else{if(weddingDateObject<weddingBuffObject){document.getElementById("error-"+n+"-1").innerHTML='<b>The date you selected is within the next 14 Days. Please consider a later date.</b>';}else{var dateParts=weddingDate.split("/");var dateObject=new Date(+dateParts[2],dateParts[1]-1,+dateParts[0]);if(dateObject.getDay()==1){var weddingDay="Monday";}else if(dateObject.getDay()==2){var weddingDay="Tuesday";}else if(dateObject.getDay()==3){var weddingDay="Wednesday";}else if(dateObject.getDay()==4){var weddingDay="Thursday";}else if(dateObject.getDay()==5){var weddingDay="Friday";}else if(dateObject.getDay()==6){var weddingDay="Saturday";}else if(dateObject.getDay()==0){var weddingDay="Sunday";}
if(dateObject.getMonth()==0){var weddingMonth="January";}else if(dateObject.getMonth()==1){var weddingMonth="February";}else if(dateObject.getMonth()==2){var weddingMonth="March";}else if(dateObject.getMonth()==3){var weddingMonth="April";}else if(dateObject.getMonth()==4){var weddingMonth="May";}else if(dateObject.getMonth()==5){var weddingMonth="June";}else if(dateObject.getMonth()==6){var weddingMonth="July";}else if(dateObject.getMonth()==7){var weddingMonth="August";}else if(dateObject.getMonth()==8){var weddingMonth="September";}else if(dateObject.getMonth()==9){var weddingMonth="October";}else if(dateObject.getMonth()==10){var weddingMonth="November";}else if(dateObject.getMonth()==11){var weddingMonth="December";}
var dataString='venue_name='+venueName+'&wedding_month='+weddingMonth+'&wedding_day='+weddingDay;$.ajax({type:"POST",url:"http://www.simplywed.co.uk/tools/quotes-demo/submit/check-date.php",data:dataString,cache:false,error:function(){document.getElementById("error-"+n+"-1").innerHTML='<b>Sorry, there seems to have been a problem checking date availability.</b>';},complete:function(response){if(response.responseText=="No"){document.getElementById("error-"+n+"-1").innerHTML='<b>We are sorry, the Wedding Date you entered is not available for this venue.</b>';}else{document.getElementById("error-"+n+"-1").innerHTML='';$("#quote-slide-"+n+"-1").hide();$("#quote-slide-"+n+"-2").show();}}});}}}}
function valDaytimeGuests(number){var n=number;var daytimeGuests=document.getElementById("daytimeGuests-"+n).value;var venuePackageGuests=document.getElementById("venuePackageGuests-"+n).value;var venueMaxDaytime=document.getElementById("venueMaxDaytime-"+n).value;if(daytimeGuests==""){document.getElementById("error-"+n+"-2").innerHTML='<b>Please enter your Daytime Guests</b>';}else{if(parseInt(daytimeGuests)<parseInt(venuePackageGuests)){document.getElementById("error-"+n+"-2").innerHTML='<b>Sorry, this package has a minimum requirement of '+parseInt(venuePackageGuests)+' Daytime Guests</b>';}else{if(parseInt(daytimeGuests)>parseInt(venueMaxDaytime)){document.getElementById("error-"+n+"-2").innerHTML='<b>Sorry, you have exceeded this venues max capacity of '+parseInt(venueMaxDaytime)+' Daytime Guests</b>';}else{document.getElementById("error-"+n+"-2").innerHTML='';$("#quote-slide-"+n+"-2").hide();$("#quote-slide-"+n+"-3").show();}}}}
function valEveningGuests(number){var n=number;var eveningGuests=document.getElementById("eveningGuests-"+n).value;var venuePackageGuests=document.getElementById("venuePackageGuests-"+n).value;var venueMaxEvening=document.getElementById("venueMaxEvening-"+n).value;if(eveningGuests==""){document.getElementById("error-"+n+"-3").innerHTML='<b>Please enter your Evening Guests</b>';}else{if(parseInt(eveningGuests)<parseInt(venuePackageGuests)){document.getElementById("error-"+n+"-3").innerHTML='<b>Sorry, this package has a minimum requirement of '+parseInt(venuePackageGuests)+' Evening Guests</b>';}else{if(parseInt(eveningGuests)>parseInt(venueMaxEvening)){document.getElementById("error-"+n+"-3").innerHTML='<b>Sorry, you have exceeded this venues max capacity of '+parseInt(venueMaxEvening)+' Evening Guests</b>';}else{document.getElementById("error-"+n+"-3").innerHTML='';$("#quote-slide-"+n+"-3").hide();$("#quote-slide-"+n+"-4").show();}}}}
