function valFirstName(number){var n=number;var firstName=document.getElementById("firstName-"+n).value;if(firstName==""){document.getElementById("error-"+n+"-5").innerHTML='<b>Please enter your First Name</b>';}else{document.getElementById("error-"+n+"-5").innerHTML='';$("#quote-slide-"+n+"-5").hide();$("#quote-slide-"+n+"-6").show();}}
function valLastName(number){var n=number;var lastName=document.getElementById("lastName-"+n).value;if(lastName==""){document.getElementById("error-"+n+"-6").innerHTML='<b>Please enter your Last Name</b>';}else{document.getElementById("error-"+n+"-6").innerHTML='';$("#quote-slide-"+n+"-6").hide();$("#quote-slide-"+n+"-7").show();}}
function valContactNumber(number){var n=number;var contactNumber=document.getElementById("contactNumber-"+n).value;if(contactNumber==""){document.getElementById("error-"+n+"-7").innerHTML='<b>Please enter your Contact Number</b>';}else{var phonePattern=/^(?:\W*\d){11}\W*$/;if(phonePattern.test(contactNumber)){document.getElementById("error-"+n+"-7").innerHTML='';$("#quote-slide-"+n+"-7").hide();$("#quote-slide-"+n+"-8").show();}else{document.getElementById("error-"+n+"-7").innerHTML='<b>Please enter a Valid Contact Number</b>';}}}
function valEmailAddress(number){var n=number;var emailAddress=document.getElementById("emailAddress-"+n).value;if(emailAddress==""){document.getElementById("error-"+n+"-8").innerHTML='<b>Please enter your Email Address</b>';}else{var emailPattern=/^(([^<>()[\]\\.,;:\s@\"]+(\.[^<>()[\]\\.,;:\s@\"]+)*)|(\".+\"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,}))$/;if(emailPattern.test(emailAddress)){document.getElementById("error-"+n+"-8").innerHTML='';submitQuote(n);$("#quote-slide-"+n+"-8").hide();$("#quote-slide-"+n+"-9").show();}else{document.getElementById("error-"+n+"-8").innerHTML='<b>Please enter a Valid Email Address</b>';}}}
function formatNumber(number){var num_parts=number.toString().split(".");num_parts[0]=num_parts[0].replace(/\B(?=(\d{3})+(?!\d))/g,",");return num_parts.join(".");}